import React, { Component } from 'react'

class App extends Component {
    state = {
        data: {},
    }
    componentDidMount() {
        const url =
            'https://api.covid19api.com/summary'
        fetch(url)
            .then((result) => result.json())
            .then((result) => {
                this.setState({
                    data: result,
                })
            })
    }

    render() {
        const { data } = this.state
        return <p>{data}</p>
    }
}

export default App
